import React, { Component } from "react";
import './App.css';
import Selector from "./Selector";
import DirList from "./DirList";
var neo4j = require('neo4j-driver')

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allDirs: [{
        name: 'Мать справочников',
        values: []
      }],
      actDirs: [],
      results: [],

    };
    
    this.driver = neo4j.driver(
      'bolt://localhost:7687',
      neo4j.auth.basic('neo4j', 'letmein')
    )
    this.xfetch('Мать справочников', this.state.allDirs);
     

  }

//   xfetch2 = async (param, target) => {
//     const res = await getRes(param)
//     this.setState({ [target]: [...target, res ] })
//  }

  xfetch = (param, target) => {
    //console.log(input)
    const session = this.driver.session({ defaultAccessMode: neo4j.session.READ });
    const res = [];
    session
    .run('MATCH (d{Name: $nameParam})-[:value]->(v) RETURN v.Name', {
      nameParam: param
    })
    .subscribe({
      //onKeys: keys => {
        //console.log(keys)
      //},
      onNext: record => {
        res.push(record._fields[0]);
        console.log(record._fields[0]);
        this.setState({ [target]: [target.push(record._fields[0])] });
      },
      onCompleted: () => {    
        
        //this.setState({ [target]: [...target, ...res ] });
        console.log(target)
        // this.setState(({ allDirs }) => {
        //   const newArr = allDirs.filter(function(item) { 
        //       return item === input
        //   });

        //   newArr.push({
        //     name: input,
        //     values: res
        //   } );    
        //   return {
        //     allDirs: newArr
        //   };
        // });
        
        session.close();// returns a Promise
        
      },
      onError: error => {
        console.log(error)
      }
    });    
  };

  addValue = input => {
    console.log(input)

  };

  render() {      
      return (      
      <main> 
             {this.state.allDirs.filter(function(item) { 
                return item.values.length > 0 && item.name !== 'Мать справочников'
            })
             .map(n => {               
          return <Selector name= {n.name} values = {n.values}  change ={this.addValue}/>//change ={this.addDir}       
       })}
        
        <Selector name= {'Справочники'} values={this.state.allDirs.filter(function(item) { 
                return item.name == 'Мать справочников'
            })[0].values} change ={this.xfetch}/> 
      </main>
    );
  }
}

export default App;
