import React, { Component } from "react";
import DirSelector from "./DirSelector";
import DirList from "./DirList";
//import { Paper } from "@material-ui/core";
class RequestHead extends Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     dirs: []
  //   };
    
  // }
  handleChange(event) {
    console.log({value: event.target.value});
    this.props.addDir({value: event.target.value});
  }
  
  oc(event){
    console.log(event.target.value);
    this.props.addDir(event.target.value)
  };

  // pickValue = input => {
    
  // };

  render() {
    //console.log(this.props);
    return (
      //  <div>
      //    <div>_RequestHead</div>

      <form onSubmit={this.oc}>
      <label>
        Выберите ваш любимый вкус:
        {/* <select >value={this.state.value} onChange={this.handleChange} */}
        <select onChange={this.handleChange}>
        {this.props.allDirs.map(n => {
         return <option value={n}>{n}</option>
       })}
          
        </select>
      </label>
      <input type="submit" value="Отправить" />
    </form>

    )
        
        
  }
}

export default RequestHead; // withStyles(styles)(UserList);
