import React, { Component } from "react";
import './App.css';
import Selector from "./Selector";
import DirList from "./DirList";
var neo4j = require('neo4j-driver')

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allDirs: [{
        name: 'bar',
        values: []
      }],
      actDirs: [],
      results: [],

    };
    
    this.driver = neo4j.driver(
      'bolt://localhost:7687',
      neo4j.auth.basic('neo4j', 'letmein')
    )
    
    this.xfetch('MATCH (x:Dir) RETURN x'); 

  }

  xfetch = (query) => {
    const session = this.driver.session({ defaultAccessMode: neo4j.session.READ });
    //const res = [];
    session
    .run(query)
    .subscribe({
      //onKeys: keys => {
        //console.log(keys)
      //},
      onNext: record => {
        //console.log(this.state.allDirs);
        //res.push(record.get('x').properties.description);    
        this.setState(({ allDirs }) => {
          const newArr = [...allDirs,{
            name: record.get('x').properties.description,
            values: []
          } ];
    
          return {
            allDirs: newArr
          };
        });

      },
      onCompleted: () => {        
        session.close();// returns a Promise
        //return res;
        
        
      },
      onError: error => {
        console.log(error)
      }
    });    
  }
  

  addDir = input => {
    console.log(input)
    const session = this.driver.session({ defaultAccessMode: neo4j.session.READ });
    const res = [];
    session
    .run('MATCH (d:Dir {description: $nameParam})-[:value]->(v:Value) RETURN v.TXTLG', {
      nameParam: input
    })
    .subscribe({
      //onKeys: keys => {
        //console.log(keys)
      //},
      onNext: record => {
        res.push(record._fields[0]);
      },
      onCompleted: () => {          
        this.setState(({ allDirs }) => {
          const newArr = allDirs.filter(function(item) { 
              return item === input
          });

          newArr.push({
            name: input,
            values: res
          } );    
          return {
            allDirs: newArr
          };
        });
        //console.log(this.state.actDirs);
        session.close();// returns a Promise
      },
      onError: error => {
        console.log(error)
      }
    });    
  };

  addValue = input => {


  };

  render() {      
      return (      
      <main> 
             {this.state.allDirs.filter(function(item) { 
                return item.values.length > 0
            })
             .map(n => {               
          return <Selector name= {n.name} values = {n.values}  change ={this.addDir}/>//change ={this.addDir}       
       })}
        
        <Selector name= {'Справочники'} values={this.state.allDirs.map(d => d.name)} change ={this.addDir}/> 
      </main>
    );
  }
}

export default App;
