import React from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";

import {
  Input,
  InputLabel,
  MenuItem,
  FormControl,
  Select,
  Paper
} from "@material-ui/core";

const GET_FIELDS = gql`
  {
    Dir {
      # name
      description
    }
  }
`;

function DirSelector({ addDir }) {
  const { loading, data, error } = useQuery(GET_FIELDS);
  //console.log(data);

  const handleChange = event => {
    addDir(event.target.value.pop());
    //console.log(event.target.value);
  };
  return (
    <Paper>
      {data && !loading && !error && (
        <FormControl>
          <InputLabel id="demo-mutiple-name-label">Name</InputLabel>
          <Select
            labelId="demo-mutiple-name-label"
            id="demo-mutiple-name"
            multiple
            value={data.Dir}
            onChange={handleChange}
            input={<Input />}
            // MenuProps={MenuProps}
          >
            {data.Dir.map(n => (
              <MenuItem key={n.description} value={n.description}>
                {n.description}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      )}
    </Paper>
  );
}

export default DirSelector; // withStyles(styles)(UserList);
